<script> 
  import { onMount } from 'svelte';
  import { gsap } from "gsap";
  import { ScrollTrigger } from "gsap/ScrollTrigger";
  import { DrawSVGPlugin } from 'gsap/all';
  import { CustomEase } from "gsap/CustomEase";
  import { CustomWiggle } from "gsap/CustomWiggle";
  import { GSDevTools } from "gsap/GSDevTools"

  gsap.registerPlugin(
    CustomEase, 
    CustomWiggle, 
    ScrollTrigger, 
    DrawSVGPlugin,
    GSDevTools
  );

  onMount(() => {
    const tl = gsap.timeline({
      scrollTrigger: {
      trigger: ".venn",
      scrub: .5,
      pin: true,
      start: "top top",
      end: "+=100%"
    }});
    const designIconTL = gsap.timeline();
    const designIconWiggle = gsap.timeline();
    const animateIconTL = gsap.timeline();
    const developIconTL = gsap.timeline();
    const magicIconTL = gsap.timeline();

    const transformOrigin = "50% 50%";
    const centered = {
      transformOrigin: "50% 50%"
    }

    const iconEase = 'power4.out',
          iconDur = .4,
          iconOverlap = '-=.2';

    const iconWord = {
      opacity: 0,
      duration: .2,
      transformOrigin,
      scale: 5,
      ease: "power1.out"
    }

    // pop-in animation for magic icon
    magicIconTL.set('#magic-set', {
      y: 90,
    })
    .from('#magic-icon-wand', {
      x: -20,
      y: 20,
      opacity: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .fromTo('.magic-icon-stems', {
      drawSVG: "0% 0%",
      opacity: 0,
    }, {
      drawSVG: "0% 100%",
      opacity: 1,
      duration: iconDur,
      ease: iconEase,
    })
    .from('.magic-icon-flowers', {
      transformOrigin,
      scale: .1,
      opacity: 0,
      stagger: .2,
      duration: iconDur,
      ease: "elastic.out(1, 0.3)",
    }, "-=.1")
    .from ('#magic-word', iconWord);

    // smashing wiggle
    // .to('.venn', {
    //   ease: "wiggle(7)",
    //   rotate: "5deg",
    //   x: "15px",
    //   y: "-8px",
    // });

    


    // pop-in animation for develop icon
    developIconTL.set('#develop-icon', {
      transformOrigin,
      x: 37,
      y: 30,
    })
    .from('#develop-icon', {
      opacity: 0, 
      scaleX: .01,
      duration: iconDur*2,
      ease: iconEase,
    })
    .from('#develop-icon', {
      scaleY: .1,
      duration: iconDur/2,
      ease: iconEase,
    })
    .from('#develop-icon-open-bracket', {
      opacity: 0,
      duration: .01,
      delay: .1,
    })
    .from('#develop-icon-slash', {
      opacity: 0,
      duration: .01,
      delay: .2,
    })
    .from('#develop-icon-close-bracket', {
      opacity: 0,
      duration: .01,
      delay: .1,
    })
    .from ('#develop-word', iconWord);


    // pop-in animation for Design icon
    designIconTL.from('#design-icon-ruler', {
      x: -40,
      y: 40,
      opacity: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .from('#design-icon-pencil', {
      x: 40,
      y: 40, 
      opacity: 0,
      duration: iconDur,
      ease: iconEase,
    }, iconOverlap)
    .from ('#design-word', iconWord);

    // pop-in animation for Animate icon
    animateIconTL
    .set('.animate-icon-line', {
      drawSVG: '0',
      opacity: 0,
    }, "0")
    .set('#animate-icon-circle', {
      x: -40,
    })
    .from('#animate-icon-circle', {
      opacity: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .to('#animate-icon-circle', {
      x: -50,
      scaleX: .8,
      duration: iconDur,
      ease: iconEase,
    }, "<")
    .from('.animate-icon-line', {
      opacity: 0,
      duration: .1,
    })
    .to('#animate-icon-circle', {
      x:0,
      duration: (iconDur*2),
      ease: iconEase,
    }, "circleStart")
    .to('.animate-icon-line', {
      drawSVG: "0 100%",
      stagger: .1,
      duration: .3,
    }, "circleStart")
    .to('#animate-icon-circle', {
      scaleX: 1.2,
      duration: (iconDur/2),
    }, "circleStart")
    .to('#animate-icon-circle', {
      scaleX: 1,
      duration: (iconDur/2),
    }, `<-=${iconDur}`)
    .from('#animate-icon-ghost2', {
      opacity: 0,
      duration: .1,
    }, `circleStart+=${iconDur/3}`)
    .from('#animate-icon-ghost1', {
      opacity: 0,
      duration: .1,
    }, `circleStart+=${(iconDur/3)*2}`)
    .from('#animate-word', iconWord);

    const duration = .8,
          durPop = 1.2,
          delay = .3,
          popOffset = "+=.2",
          iconDelay = "-=.2";

    tl.from('.circle-design', {
      duration,
      opacity: 0,
    })
    .from('.circle-design', {
      duration: durPop,
      scale: .1,
      transformOrigin: "50% 50%",
      ease: "elastic.out(1, 0.3)",
    }, "<")
    .add(designIconTL, iconDelay)
    .from('.circle-animate', {
      duration,
      opacity: 0,
    }, popOffset)
    .from('#animate-set', {
      duration,
      opacity: 0,
    }, "<")
    .from('.circle-animate', {
      duration: durPop,
      scale: .1,
      transformOrigin,
      ease: "elastic.out(1, 0.3)",
    }, "<")
    .add(animateIconTL, iconDelay)
    .from('.circle-develop', {
      duration,
      opacity: 0,
    }, popOffset)
    .from('#develop-set', {
      duration,
      opacity: 0
    }, "<")
    .from('.circle-develop', {
      duration: durPop,
      scale: .1,
      transformOrigin,
      ease: "elastic.out(1, 0.3)",
    }, "<")
    .add(developIconTL, iconDelay)
    .to('.whole-design', {
      duration,
      delay: 1.2,
      y: 80,
    })
    .to('#design-set', {
      duration,
      y: -8
    }, "<")
    .to('.whole-animate', {
      duration,
      y: -80,
      x: 95
    }, popOffset)
    .to('#animate-set', {
      duration,
      y: -20,
      x: 40
    }, "<")
    .to('.whole-develop', {
      duration,
      y: -80,
      x: -95
    }, popOffset)
    .to('#develop-set', {
      duration,
      y: -20,
      x: -40
    }, "<")
    .from('#magic-set', {
      duration,
      delay,
      opacity: 0,
    })
    .add(magicIconTL, iconDelay);

    ScrollTrigger.create({
    trigger: ".venn",
    start: "top top", 
    end: "bottom 150px",
    pin: "#orange-content"
    });

    GSDevTools.create({animation: tl})
  });
</script>

