<script> 
  import { onMount } from 'svelte';
  import { gsap } from "gsap";
  import { ScrollTrigger } from "gsap/ScrollTrigger";
  import { DrawSVGPlugin } from 'gsap/all';
  import { GSDevTools } from "gsap/GSDevTools"
  import { Physics2DPlugin } from "gsap/Physics2DPlugin"
  import DashedCircle from './dashedCircle.svelte';

  gsap.registerPlugin(
    ScrollTrigger, 
    DrawSVGPlugin,
    GSDevTools,
    Physics2DPlugin
  );

  onMount(() => {
    const tl = gsap.timeline({
      scrollTrigger: {
      trigger: ".venn",
      markers: true,
      start: "-40% top",
    }});
    const designIconTL = gsap.timeline();
    const designIconWiggle = gsap.timeline();
    const animateIconTL = gsap.timeline();
    const developIconTL = gsap.timeline();
    const magicIconTL = gsap.timeline();

    const transformOrigin = "50% 50%";
    const iconEase = 'power4.out',
          iconDur = .4,
          iconOverlap = '-=.2';

    const iconWord = {
      autoAlpha: 0,
      duration: .2,
      transformOrigin,
      scale: 5,
      ease: "power1.out"
    }

    // pop-in animation for magic icon
    const show = { autoAlpha: 1 };
    const petalFlyer = {
      autoAlpha: 0,
      ease: "power4.out",
      duration: 4, 
      rotate: "random(200, 400)",
      physics2D: {
        velocity: "random(200, 400)",
        gravity: 400,
        angle: "random(-110, 90, 5)",
      },
    };
    const physics2D = {
      velocity: 300,
      gravity: 400,
      angle: () => {gsap.random(0, 100, 5)}
    };

    gsap.set('.petal-flyers', {
      autoAlpha: 0,
    })

    magicIconTL.set('#magic-set', {
      y: 90,
    })
    .from('#magic-icon-wand', {
      x: -20,
      y: 20,
      autoAlpha: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .fromTo('.magic-icon-stems', {
      drawSVG: "0% 0%",
      autoAlpha: 0,
    }, {
      drawSVG: "0% 100%",
      autoAlpha: 1,
      duration: iconDur,
      ease: iconEase,
    }, "flowers")
    .from('.magic-icon-flowers', {
      transformOrigin,
      scale: .1,
      autoAlpha: 0,
      stagger: .2,
      duration: iconDur,
      ease: "elastic.out(1, 0.3)",
    }, "-=.1")
    .set('#mirp1', show, "flowers+=.4")
    .to('#mirp1', petalFlyer, "flowers+=.4")
    .set('#mirp3', show, "flowers+=.4")
    .to('#mirp3', petalFlyer, "flowers+=.4")
    .set('#milp5', show, "flowers+=.6")
    .to('#milp5', petalFlyer, "flowers+=.6")
    .set('#milp1', show, "flowers+=.6")
    .to('#milp1', petalFlyer, "flowers+=.6")
    .set('#micp4', show, "flowers+=.8")
    .to('#micp4', petalFlyer, "flowers+=.8")
    .set('#micp2', show, "flowers+=.8")
    .to('#micp2', petalFlyer, "flowers+=.8")
    .from('#magic-word', iconWord, "flowers+=1");

   
    // mirp1 mirp3 <.3 milp5 milp1 <.9 micp4 micp2 <1.5

    // pop-in animation for develop icon
    developIconTL.set('#develop-icon', {
      transformOrigin,
      x: 37,
      y: 30,
    })
    .from('#develop-icon', {
      autoAlpha: 0, 
      scaleX: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .from('#develop-icon', {
      scaleY: .1,
      duration: iconDur/2,
      ease: iconEase,
    })
    .from('#develop-icon-open-bracket', {
      autoAlpha: 0,
      duration: .01,
      delay: .1,
    })
    .from('#develop-icon-slash', {
      autoAlpha: 0,
      duration: .01,
      delay: .2,
    })
    .from('#develop-icon-close-bracket', {
      autoAlpha: 0,
      duration: .01,
      delay: .1,
    })
    .from('#develop-word', iconWord);

    // pop-in animation for Design icon
    designIconTL.from('#design-icon-ruler', {
      x: -40,
      y: 40,
      autoAlpha: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .from('#design-icon-pencil', {
      x: 40,
      y: 40, 
      autoAlpha: 0,
      duration: iconDur,
      ease: iconEase,
    }, iconOverlap)
    .from ('#design-word', iconWord);

    // pop-in animation for Animate icon
    animateIconTL
    .set('.animate-icon-line', {
      drawSVG: '0',
      autoAlpha: 0,
    }, "0")
    .set('#animate-icon-circle', {
      x: -40,
    })
    .from('#animate-icon-circle', {
      autoAlpha: 0,
      duration: iconDur,
      ease: iconEase,
    })
    .to('#animate-icon-circle', {
      x: -50,
      delay: .1,
      scaleX: .8,
      duration: iconDur,
      ease: iconEase,
    }, "<")
    .from('.animate-icon-line', {
      autoAlpha: 0,
      duration: .1,
    }, "circleStart")
    .to('#animate-icon-circle', {
      x:0,
      duration: (iconDur*2),
      ease: iconEase,
    })
    .to('.animate-icon-line', {
      drawSVG: "0 100%",
      stagger: .1,
      duration: .3,
    }, "circleStart+=.2")
    .to('#animate-icon-circle', {
      scaleX: 1.2,
      duration: (iconDur/2),
      ease: "none",
    }, "circleStart")
    .to('#animate-icon-circle', {
      scaleX: 1,
      duration: (iconDur/2),
      ease: "none",
    }, "circleStart+=.2")
    .from('#animate-icon-ghost2', {
      autoAlpha: 0,
      duration: .1,
    }, `circleStart+=${iconDur/3}`)
    .from('#animate-icon-ghost1', {
      autoAlpha: 0,
      duration: .1,
    }, `circleStart+=${(iconDur/3)*2}`)
    .from('#animate-word', iconWord)
    .timeScale(1.4);

    // whole venn circles animation
    const duration = .6,
          burstFrom = {
            // autoAlpha: 0,
            // scale: 0.1,
            // transformOrigin,
          },
          burstTo = {
            // duration: 1.2,
            // autoAlpha: 0,
            // scale: 1.4,
            // rotate: 180,
            // transformOrigin,
            // ease: "power3.inOut",
          },
          oranges = {
            duration,
            autoAlpha: 0,
            scale: .4,
            transformOrigin,
            ease: "power2.inOut",
          },
          innerCircles = {
            autoAlpha: 0,
            duration,
            scale: .5,
            transformOrigin,
            delay: .2,
            ease: "power2.out",
          },
          outerCircleOffset = "-=.7",
          innerCircleOffset = "-=.6",
          iconOffset = "-=.2",
          popOffset = "-=.4",
          moveOffset = "+=.2";
    
    tl.fromTo('#orange-b-1', burstFrom, burstTo)
    .from('#orange-1', oranges, outerCircleOffset)
    .from('.circle-design', innerCircles, innerCircleOffset)
    .add(designIconTL, iconOffset)
    .fromTo('#orange-b-2', burstFrom, burstTo, popOffset)
    .from('#orange-2', oranges, outerCircleOffset)
    .from('.circle-animate', innerCircles, innerCircleOffset)
    .add(animateIconTL, iconOffset)
    .fromTo('#orange-b-3', burstFrom, burstTo, popOffset)
    .from('#orange-3', oranges, outerCircleOffset)
    .from('.circle-develop', innerCircles, innerCircleOffset)
    .add(developIconTL, iconOffset)
    .to('.circle-design-w', {
      duration,
      delay: .4,
      y: 80,
    })
    .to('#design-set', {
      duration,
      y: -8
    }, "<")
    .to('.circle-animate-w', {
      duration,
      y: -80,
      x: 95
    }, moveOffset)
    .to('#animate-set', {
      duration,
      y: -20,
      x: 40
    }, "<")
    .to('.circle-develop-w', {
      duration,
      y: -80,
      x: -95
    }, moveOffset)
    .to('#develop-set', {
      duration,
      y: -20,
      x: -40
    }, "<")
    .add(magicIconTL);

    
    //slight parallax to delay scroll
    // gsap.to(".venn", {
    //   yPercent: 30,
    //   ease: "none",
    //   scrollTrigger: {
    //     trigger: ".venn",
    //     // start: "top bottom", // the default values
    //     // end: "bottom top",
    //     scrub: true
    //   }, 
    // });

    // dev slider
    GSDevTools.create({animation: tl})
  });
</script>

